import React, { useState, useEffect, useCallback } from 'react';

import UserProfile from '../../containers/UserProfile';
import UserPosts from '../../containers/UserPosts';

import Loading from '../../components/Loading';

const ProfileRoute = ({match}) => {
  const [isLoading, setisLoading] = useState(true);
  const [posts, setPosts] = useState([]);
  const [user, setUser] = useState({});
  const [users, setUsers] = useState([]);  

  const findUserByUsername = useCallback(
    (username) => {
      const result = users.filter((user) => user.username === username);
      return result.length > 0 ? result[0] : {
        id: "", 
        name: `USER NOT FOUND (username = ${username})`,
        avatar: "",
        username: username,
        email: ""
      };
    },
    [users]
  )

  // api call to get users
  useEffect(() => {
    fetch("https://5e7d0266a917d70016684219.mockapi.io/api/v1/users")
    .then(res => res.json())
    .then(response => {
      setUsers(response);
      const userFound = findUserByUsername(match.params.username);
      console.log(userFound);
      setUser(userFound);
      setisLoading(false); 
    });
  }, [user, findUserByUsername, match]);

   // api call to get posts
   useEffect(() => {
    fetch("https://5e7d0266a917d70016684219.mockapi.io/api/v1/users/1/posts")
    .then(res => res.json())
    .then(response => {
      setPosts(response);
      setisLoading(false);
    })
  }, []);

  return (
    <div data-testid="profile-route">
      {isLoading && <Loading />}
      {!isLoading && (
        <React.Fragment>
          <UserProfile {...user} />
          <UserPosts posts={posts} userInfo={user} />
        </React.Fragment>
      )}
    </div>
  );
};

export default ProfileRoute;
